sys_script_email.script:
(function getLatestComment() {

	var comment = getJournalEntry();

	if (comment != "") {
		template.print(comment);
	}
}());

function getJournalEntry() {

	var obj = new global.FGEgetJournalEntry(),
		na = obj.getCommentsEntry().split("\n\n"),
		result = "";

	for (var i = 0; i < na.length; i++) {

		//<div style=""><span class="tdwrap"><strong>02-09-2015 12:24:24 BST - Gues</strong></span>	
	//<span style="float:right;"><sup>Additional comments</sup></span></div>

		result += '<div><span style="font-family: verdana, geneva;">' + na[i] + '</span></div>';
		result += '<div><span style="font-family: verdana, geneva;">&nbsp;</span></div>';
	}
	return result;
}
